 function App() {
  const dispatch = useDispatch();
  const showHamburger = useSelector((state) => state.hamburger.showHamburger); //automatically manages subscription
  return (
    <div>
      <Menubar/>
      {showHamburger && <Hamburgermenu/>}

      {/* teaser */}
      {/* description */}
      {/* pricing */}
      {/* amount of product */}
      {/* add to cart button */}
      {/* attribution */}
    </div>
  );
}

export default App;
